<?php

namespace App;

use Illuminate\Database\Eloquent\Model;

class Task extends Model
{
    protected $guarded = [];

    protected $touches = [ 'project' ];

    protected $casts = [

        'completed' => 'boolean'
    ];

    // Overriding the 'boot' method in a model is a way to override the various lifecycle hooks of a model within it.
